import { createStore } from "redux";

const reducer = (state = { value: 0 }, action) => {
  switch (action.type) {
    case "INCREMENT":
      return { value: state.value + action.amount};
    case "DECREMENT":
      return { value: state.value - 1 };
    case "INCREASE":

    default:
      return state;
  }
};

const store = createStore(reducer, { value: 0 });

export default store;
